import { Link,redirect,useParams, useSubmit } from 'react-router-dom';
import classes from './EventItem.module.css';

function EventItem({ event }) {
  const submit = useSubmit();
  const id = useParams().eventId ;

  function startDeleteHandler() {
    const proceed = window.confirm(`Do you Want To Delete <<${event.title}>> Item ? `) ;
    if (proceed) {
      submit(null, { method : "DELETE" })
    }
  }

  return (
    <article className={classes.event}>
      <img src={event.image} alt={event.title} />
      <h1>{event.title}</h1>
      <time>{event.date}</time>
      <p>{event.description}</p>
      <menu className={classes.actions}>
        <Link to={`/events/${id}/edit`}> Edit </Link>
        <button onClick={startDeleteHandler}>Delete</button>
      </menu>
    </article>
  );
}

export default EventItem;


async function loadEvents(){
  const response = await fetch('http://localhost:8080/events') ;

  if(!response.ok) {
    throw json({ message:"Error need to be fixed ðŸ›  !! -- Failed To Fetch" } ,{
      status : 500
    })
  }

  // return response ; //when we using defer we can't return a response ... we make await resonse.json();
  const resData = await response.json();
  return resData.events ;
}
