import { Form, useNavigate , useNavigation,json, redirect } from 'react-router-dom';

import classes from './EventForm.module.css';

function EventForm({ method, event }) {
  const navigate = useNavigate();
  function cancelHandler() {
    navigate('/events');
  }

  const isSubmitting = useNavigation().state === 'submitting' ;
  const eventObject = {} ;
async function handelFetch(){
    const response = event.then((data)=>{
      newfunction(data)
    })
  }
handelFetch() ;


function newfunction(myData){
  eventObject.title = myData.title ;
  eventObject.image = myData.image ;
  return myData ;
}



  return (
    <Form method={method} className={classes.form} >
      <p>
        <label htmlFor="title">Title</label>
        <input id="title" type="text" name="title" required defaultValue = {event ? event.title : ""} />
      </p>
      <p>
        <label htmlFor="image">Image</label>
        <input id="image" type="url" name="image" required defaultValue = {event ? event.image : ""}/>
      </p>
      <p>
        <label htmlFor="date">Date</label>
        <input id="date" type="date" name="date" required defaultValue = {event ? event.date : ""}/>
      </p>
      <p>
        <label htmlFor="description">Description</label>
        <textarea id="description" name="description" rows="5" required defaultValue = {event ? event.description : ""}/>
      </p>
      <div className={classes.actions}>
        <button type="button" onClick={cancelHandler}>Cancel</button>
        <button>{isSubmitting ? "isSubmitting" : "Save"}</button>
      </div>
    </Form>
  );
}

export default EventForm;



export async function action({request,params}){
  let url = "http://localhost:8080/events" ;
  const method = request.method ;
  const id = params.eventId ;

  const data = await request.formData();

  const eventData = {
      title : data.get('title'),
      image : data.get('image'),
      date : data.get('date'),
      description : data.get('description')
  } ;



  if(method === "PATCH") {
    url = "http://localhost:8080/events/"+ id ;
  }
  const response = await fetch(url , {
    method : method ,
    headers : {
      'Content-Type' : 'application/json'
    } ,
    body : JSON.stringify(eventData)
  })

  if(response.status === 422){ // 422 is in backend here so not common
    return response ;
  }

  if(!response.ok) {
    throw json({message: "Failed to Did the Process ðŸ› "},{
        status : 500
    })
} else return redirect("/events")

}
